import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
/*import data from '../data';*/
import axios from 'axios';

function HomeScreen (props)
{
    const [products, setProduct] = useState([]);

    useEffect(() => {
        const fetchData = async () =>{
            const {data} = await axios.get("/api/products");
            setProduct(data);
        }
        fetchData();
        return () => {

        };
    }, [])

    return  <ul class="products">
    {
        products.map(product => 
        <li>
            <div class="product">
            <Link to={'/product/' + product._id}><img class="product-image" src={product.image} alt="product"/></Link>
                
                <div class="product-name">
                    <Link to={'/product/' + product._id}>{product.name}</Link>
                </div>
                <div class="product-brand">{product.brand}</div>
                <div class="product-price">{product.price}DT</div>
                <div class="product-rating">{product.rating} Stars ({product.numReviews} Reviews)</div>
            </div>
        </li>)
    }
        
     </ul>

}
export default HomeScreen;
