import React, { useEffect, useState } from 'react';
import { Link, Route } from 'react-router-dom';
/*import data from '../data';**/
import { useDispatch, useSelector } from 'react-redux';
import { listProducts } from '../actions/productActions.js';
// function save_id(x) 
// {
//      localStorage.setItem('id',x)
// }
function HomeScreen (props)
{
    /*const [products, setProduct] = useState([]);*/
    const productList = useSelector(state => state.productList);
    const { products, loading, error } = productList;
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(listProducts());
        return () => {
            //
        };
    }, []);
    return loading? <div>Loading...</div>:
    error? <div>{error}</div>:
    <ul className="products">
    {
        products.map(product =>
        <li key={product._id}>
            <div className="product">
            <Link to={'/product/' + product._id}>
                  <img
                    className="product-image"
                    src={product.image}
                    alt="product"
                  />
                </Link>

            {/* <Link to={'/product/' + product._id} onClick={()=>save_id(product._id)} ><img className="product-image" src={product.image} alt="product"/></Link> */}
                
                <div className="product-name">
                    <Link  to={'/product/' + product._id} onClick={()=>save_id(product._id)} >{product.name}</Link>
                    
                </div>
                <div className="product-brand">{product.brand}</div>
                <div className="product-price">{product.price}DT</div>
                <div className="product-rating">{product.rating} Stars ({product.numReviews} Reviews)</div>
            </div>
        </li>)
    }
        
     </ul>

}
export default HomeScreen;
